	};

	$('#datepicker').datepicker({
	    language: "ru",
	    forceParse: false,
	    daysOfWeekDisabled: "0",
	    calendarWeeks: true,
	    autoclose: true,
	    todayHighlight: true,
	    startDate: "01.09.2015",
